mapper.select:
		select b_no, b_title, m_id, b_date, b_cnt, r
		from (
   			select b_no, b_title, m_id, b_date, b_cnt, rownum r
    		from(
       			 select b_no, b_title, m_id, b_date, b_cnt
        		 from board
        		 
        		 	
        		 		
        		 			b_title like '%'||#{word}||'%'
        		 		
        		 		
        		 			b_content like '%'||#{word}||'%'
        		 		
        		 		
        		 			m_id like '%'||#{word}||'%'
        		 		
        		 		
        		 			b_title like '%'||#{word}||'%'
        		 			or
        		 			b_content like '%'||#{word}||'%'
        		 		
        		 	
        		 
        		 order by b_ref desc, b_ansnum
     	  	 	)
    		)
    	
		where r >= #{sno} and r <= #{eno}

select b_no, b_title, b_content, b_date, b_cnt, b_filename, m_id
from board
where b_no = #{b_no}

mapper.update:
update board
set b_cnt = b_cnt + 1
where b_no = #{b_no}

update board
set
	b_title = #{b_title},
	b_content = #{b_content},
	b_filename = #{b_filename}
where b_no = #{b_no}

mapper.select:
select count(b_refnum)
from board
where b_refnum = #{b_no}
