mapper.select:
		select br_no, br_content, to_char(br_date, 'yyyy-mm-dd') br_date, m_id, b_no, r
		from(
   			 select br_no, br_content, br_date, m_id, b_no, rownum r
  			 from(
       			 select br_no, br_content, br_date, m_id, b_no
       			 from board_reply
       			 	where b_no = #{b_no}
       			 	order by br_no desc
       			 )
   		 	)
   		
		where r > = #{sno} and r<= #{eno}

mapper.update:
		update board_reply
		set
  	 	br_content = #{br_content}
		where br_no = #{br_no}
