mapper.select:
	
        
        select t.*, r 
			from(
        		select t.*, rownum r 
       			 from(
               			 select f1.f_no, f1.f_date, f1.f_id, f1.f_amount, f1.f_total, f1.m_id , f2.f_title, to_char(f2.f_sdate,'yyyy-mm-dd') f_sdate , to_char(f2.f_edate,'yyyy-mm-dd') f_edate, f2.f_price , f2.F_IMAGE
               			 from favorite f1 inner join festival f2
               			 on f1.f_id = f2.f_id 
               			 where f1.m_id = #{m_id}
                		 order by f_date desc
           			 ) t            
     			) t
		
		where r >= #{sno} and r <= #{eno}

mapper.update:
update favorite
set f_amount = #{amount}
where m_id = #{m_id} and f_id = #{f_id}
